#include <iostream>
#include <cassert>
#include "src.cpp"

int main() {
    string fst_str = "ssaaad";
    HuffmanEncoder huff1(fst_str);
    assert(huff1.encode_message() == "111100010");
    assert(huff1.decode_message("111100010") == fst_str);

    string scd_str = "Lorem ipsum dolor sit amet, consectetur adipiscing elit. In aliquam auctor libero a facilisis. Nam at sem vel orci lobortis tempus id eu ligula. Vestibulum ligula arcu, tristique at dolor quis, condimentum tincidunt orci. Duis non ligula a ex lobortis blandit a ut orci. Quisque lacinia, felis tincidunt sagittis vestibulum, dolor orci bibendum nibh, in imperdiet ipsum erat sed est. Quisque nec condimentum erat, in tristique felis. Praesent mauris dui, lobortis at urna sed, dapibus venenatis odio. Nullam sit amet scelerisque risus, a lacinia neque. Donec eleifend massa dictum metus dignissim pulvinar. Ut volutpat ligula sapien, eget ornare ipsum egestas quis. Nunc venenatis ornare arcu, et vehicula sapien. Proin dignissim eros dui, at ultrices urna fringilla vitae. Quisque gravida placerat metus. Nullam efficitur maximus turpis, ac iaculis lectus rhoncus ac.";
    HuffmanEncoder huff2(scd_str);
    assert(huff2.encode_message() == "");
    assert(huff2.decode_message("") == scd_str);

    cout << "Тесты пройдены успешно!";
    return 0;
}